import type { NextConfig } from "next";

const nextConfig: NextConfig = {
  /* config options here */
};

export default nextConfig;

@import "tailwindcss";

@layer tokens {
  :root {
    /* ===== PERANAKAN HERITAGE COLOR SYSTEM ===== */
    --color-nyonya-cream: #F8F3E6;
    --color-nyonya-cream-rgb: 248, 243, 230;

    --color-kopi-brown: #3A2A1F;
    --color-kopi-brown-rgb: 58, 42, 31;

    --color-terracotta: #C77966;
    --color-terracotta-rgb: 199, 121, 102;

    --color-heritage-blue: #4A6B7D;
    --color-heritage-blue-rgb: 74, 107, 125;

    --color-gold-leaf: #D4AF37;
    --color-gold-leaf-rgb: 212, 175, 55;

    /* ===== ACCESSIBILITY-SAFE VARIANTS ===== */
    --color-ui-terracotta: #9A5E4A;
    --color-ui-gold: #A68A2E;
    --color-ui-blue: #3A5463;

    /* ===== TYPOGRAPHY ===== */
    --font-heading: var(--font-cormorant-garamond);
    --font-body: var(--font-crimson-pro);
    --font-decorative: var(--font-pinyon-script);

    /* ===== FLUID TYPOGRAPHY SCALE (Major Third 1.25) ===== */
    --text-xs: clamp(0.69rem, 0.66rem + 0.17vw, 0.79rem);
    --text-sm: clamp(0.83rem, 0.79rem + 0.21vw, 0.96rem);
    --text-base: clamp(1.00rem, 0.95rem + 0.26vw, 1.20rem);
    --text-lg: clamp(1.20rem, 1.14rem + 0.31vw, 1.44rem);
    --text-xl: clamp(1.44rem, 1.37rem + 0.37vw, 1.73rem);
    --text-2xl: clamp(1.73rem, 1.64rem + 0.45vw, 2.07rem);
    --text-3xl: clamp(2.07rem, 1.97rem + 0.54vw, 2.49rem);
    --text-4xl: clamp(2.49rem, 2.37rem + 0.65vw, 3.20rem);
    --text-5xl: clamp(3.00rem, 2.80rem + 0.80vw, 4.50rem);
    --text-6xl: clamp(3.50rem, 3.20rem + 1.00vw, 5.50rem);

    /* ===== SPACING ===== */
    --space-1: 0.25rem;
    --space-2: 0.5rem;
    --space-3: 0.75rem;
    --space-4: 1rem;
    --space-6: 1.5rem;
    --space-8: 2rem;
    --space-12: 3rem;
    --space-16: 4rem;
    --space-24: 6rem;
    --space-32: 8rem;

    /* ===== ANIMATIONS ===== */
    --duration-fast: 120ms;
    --duration-medium: 250ms;
    --duration-slow: 400ms;
    --easing-smooth: cubic-bezier(0.25, 0.46, 0.45, 0.94);

    /* ===== SHADOWS ===== */
    --shadow-sm: 0 2px 4px rgba(var(--color-kopi-brown-rgb), 0.08);
    --shadow-lg: 0 8px 16px rgba(var(--color-kopi-brown-rgb), 0.15);
  }

  @media (prefers-contrast: more) {
    :root {
      --color-nyonya-cream: #FFFFFF;
      --color-kopi-brown: #000000;
      --color-ui-terracotta: #000000;
      --color-ui-gold: #000000;
      --color-ui-blue: #000000;
    }
  }

  @media (prefers-reduced-motion: reduce) {
    :root {
      --duration-fast: 1ms;
      --duration-medium: 1ms;
      --duration-slow: 1ms;
    }
  }
}

@theme {
  --color-nyonya-cream: var(--color-nyonya-cream);
  --color-kopi-brown: var(--color-kopi-brown);
  --color-terracotta: var(--color-terracotta);
  --color-heritage-blue: var(--color-heritage-blue);
  --color-gold-leaf: var(--color-gold-leaf);

  --color-ui-terracotta: var(--color-ui-terracotta);
  --color-ui-gold: var(--color-ui-gold);
  --color-ui-blue: var(--color-ui-blue);

  --font-heading: var(--font-heading);
  --font-body: var(--font-body);
  --font-decorative: var(--font-decorative);

  --text-xs: var(--text-xs);
  --text-sm: var(--text-sm);
  --text-base: var(--text-base);
  --text-lg: var(--text-lg);
  --text-xl: var(--text-xl);
  --text-2xl: var(--text-2xl);
  --text-3xl: var(--text-3xl);
  --text-4xl: var(--text-4xl);
  --text-5xl: var(--text-5xl);
  --text-6xl: var(--text-6xl);

  --animate-float: float 3s ease-in-out infinite;
  --animate-fade-up: fadeUp var(--duration-slow) var(--easing-smooth) forwards;
}

@layer base {
  body {
    background-color: var(--color-nyonya-cream);
    color: var(--color-kopi-brown);
    font-family: var(--font-body);
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-heading);
    font-weight: 700;
  }
}

@layer components {
  .folio-frame {
    position: relative;
    border: 1px solid rgba(var(--color-kopi-brown-rgb), 0.1);
    border-radius: 8px;
    overflow: hidden;
    transition: all var(--duration-medium) var(--easing-smooth);
  }

  .folio-frame::after {
    content: '';
    position: absolute;
    inset: 8px;
    border: 1px solid rgba(var(--color-gold-leaf-rgb), 0.2);
    border-radius: 6px;
    pointer-events: none;
  }

  .folio-frame:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-lg);
    border-color: var(--color-ui-terracotta);
  }

  .folio-frame:hover::after {
    border-color: var(--color-gold-leaf);
  }
}

@layer overrides {
  @keyframes float {
    0%, 100% { transform: translateY(0) rotate(0deg); opacity: 0.3; }
    50% { transform: translateY(-20px) rotate(15deg); opacity: 0.6; }
  }

  @keyframes fadeUp {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
}

export const tokens = {
  colors: {
    nyonyaCream: {
      DEFAULT: '#F8F3E6',
      rgb: '248, 243, 230',
    },
    kopiBrown: {
      DEFAULT: '#3A2A1F',
      rgb: '58, 42, 31',
    },
    terracotta: {
      DEFAULT: '#C77966',
      rgb: '199, 121, 102',
    },
    heritageBlue: {
      DEFAULT: '#4A6B7D',
      rgb: '74, 107, 125',
    },
    goldLeaf: {
      DEFAULT: '#D4AF37',
      rgb: '212, 175, 55',
    },
    ui: {
      terracotta: '#9A5E4A',
      gold: '#A68A2E',
      blue: '#3A5463',
    },
  },
  typography: {
    fonts: {
      heading: 'var(--font-heading)',
      body: 'var(--font-body)',
      decorative: 'var(--font-decorative)',
    },
    scale: {
      xs: 'var(--text-xs)',
      sm: 'var(--text-sm)',
      base: 'var(--text-base)',
      lg: 'var(--text-lg)',
      xl: 'var(--text-xl)',
      '2xl': 'var(--text-2xl)',
      '3xl': 'var(--text-3xl)',
      '4xl': 'var(--text-4xl)',
      '5xl': 'var(--text-5xl)',
      '6xl': 'var(--text-6xl)',
    },
  },
  spacing: {
    1: '0.25rem',
    2: '0.5rem',
    3: '0.75rem',
    4: '1rem',
    6: '1.5rem',
    8: '2rem',
    12: '3rem',
    16: '4rem',
    24: '6rem',
    32: '8rem',
  },
  animations: {
    duration: {
      fast: '120ms',
      medium: '250ms',
      slow: '400ms',
    },
    easing: {
      smooth: 'cubic-bezier(0.25, 0.46, 0.45, 0.94)',
    },
  },
} as const;

import { type ClassValue, clsx } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

import type { Metadata } from "next";
import { Cormorant_Garamond, Crimson_Pro, Pinyon_Script } from "next/font/google";
import "./globals.css";

const cormorantGaramond = Cormorant_Garamond({
  variable: "--font-cormorant-garamond",
  subsets: ["latin"],
  weight: ["400", "600", "700"],
});

const crimsonPro = Crimson_Pro({
  variable: "--font-crimson-pro",
  subsets: ["latin"],
  weight: ["400", "600"],
});

const pinyonScript = Pinyon_Script({
  variable: "--font-pinyon-script",
  subsets: ["latin"],
  weight: "400",
});

export const metadata: Metadata = {
  title: "Merlion Brews",
  description: "Artisan Coffee Crafted with Peranakan Soul",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en-SG">
      <body
        className={`${cormorantGaramond.variable} ${crimsonPro.variable} ${pinyonScript.variable} antialiased`}
      >
        <div id="skip-nav" className="sr-only focus-within:not-sr-only focus-within:absolute focus-within:z-50 focus-within:bg-white focus-within:p-4">
          <a href="#main-content">Skip to content</a>
        </div>
        {children}
      </body>
    </html>
  );
}

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90",
        outline:
          "border border-input bg-background shadow-sm hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-md px-3 text-xs",
        lg: "h-10 rounded-md px-8",
        icon: "h-9 w-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  }
)
Button.displayName = "Button"

export { Button, buttonVariants }

import * as React from "react"

import { cn } from "@/lib/utils"

const Card = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn(
      "rounded-xl border bg-card text-card-foreground shadow",
      className
    )}
    {...props}
  />
))
Card.displayName = "Card"

const CardHeader = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex flex-col space-y-1.5 p-6", className)}
    {...props}
  />
))
CardHeader.displayName = "CardHeader"

const CardTitle = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("font-semibold leading-none tracking-tight", className)}
    {...props}
  />
))
CardTitle.displayName = "CardTitle"

const CardDescription = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
CardDescription.displayName = "CardDescription"

const CardContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div ref={ref} className={cn("p-6 pt-0", className)} {...props} />
))
CardContent.displayName = "CardContent"

const CardFooter = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => (
  <div
    ref={ref}
    className={cn("flex items-center p-6 pt-0", className)}
    {...props}
  />
))
CardFooter.displayName = "CardFooter"

export { Card, CardHeader, CardFooter, CardTitle, CardDescription, CardContent }

"use client"

import * as React from "react"
import * as SheetPrimitive from "@radix-ui/react-dialog"
import { cva, type VariantProps } from "class-variance-authority"
import { X } from "lucide-react"

import { cn } from "@/lib/utils"

const Sheet = SheetPrimitive.Root

const SheetTrigger = SheetPrimitive.Trigger

const SheetClose = SheetPrimitive.Close

const SheetPortal = SheetPrimitive.Portal

const SheetOverlay = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Overlay
    className={cn(
      "fixed inset-0 z-50 bg-black/80 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
      className
    )}
    {...props}
    ref={ref}
  />
))
SheetOverlay.displayName = SheetPrimitive.Overlay.displayName

const sheetVariants = cva(
  "fixed z-50 gap-4 bg-background p-6 shadow-lg transition ease-in-out data-[state=closed]:duration-300 data-[state=open]:duration-500 data-[state=open]:animate-in data-[state=closed]:animate-out",
  {
    variants: {
      side: {
        top: "inset-x-0 top-0 border-b data-[state=closed]:slide-out-to-top data-[state=open]:slide-in-from-top",
        bottom:
          "inset-x-0 bottom-0 border-t data-[state=closed]:slide-out-to-bottom data-[state=open]:slide-in-from-bottom",
        left: "inset-y-0 left-0 h-full w-3/4 border-r data-[state=closed]:slide-out-to-left data-[state=open]:slide-in-from-left sm:max-w-sm",
        right:
          "inset-y-0 right-0 h-full w-3/4 border-l data-[state=closed]:slide-out-to-right data-[state=open]:slide-in-from-right sm:max-w-sm",
      },
    },
    defaultVariants: {
      side: "right",
    },
  }
)

interface SheetContentProps
  extends React.ComponentPropsWithoutRef<typeof SheetPrimitive.Content>,
    VariantProps<typeof sheetVariants> {}

const SheetContent = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Content>,
  SheetContentProps
>(({ side = "right", className, children, ...props }, ref) => (
  <SheetPortal>
    <SheetOverlay />
    <SheetPrimitive.Content
      ref={ref}
      className={cn(sheetVariants({ side }), className)}
      {...props}
    >
      <SheetPrimitive.Title className="sr-only">Menu</SheetPrimitive.Title>
      <SheetPrimitive.Description className="sr-only">Navigation links</SheetPrimitive.Description>
      {children}
      <SheetPrimitive.Close className="absolute right-4 top-4 rounded-sm opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none data-[state=open]:bg-secondary">
        <X className="h-4 w-4" />
        <span className="sr-only">Close</span>
      </SheetPrimitive.Close>
    </SheetPrimitive.Content>
  </SheetPortal>
))
SheetContent.displayName = SheetPrimitive.Content.displayName

const SheetHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col space-y-2 text-center sm:text-left",
      className
    )}
    {...props}
  />
)
SheetHeader.displayName = "SheetHeader"

const SheetFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      "flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2",
      className
    )}
    {...props}
  />
)
SheetFooter.displayName = "SheetFooter"

const SheetTitle = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Title>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Title
    ref={ref}
    className={cn("text-lg font-semibold text-foreground", className)}
    {...props}
  />
))
SheetTitle.displayName = SheetPrimitive.Title.displayName

const SheetDescription = React.forwardRef<
  React.ElementRef<typeof SheetPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof SheetPrimitive.Description>
>(({ className, ...props }, ref) => (
  <SheetPrimitive.Description
    ref={ref}
    className={cn("text-sm text-muted-foreground", className)}
    {...props}
  />
))
SheetDescription.displayName = SheetPrimitive.Description.displayName

export {
  Sheet,
  SheetPortal,
  SheetOverlay,
  SheetTrigger,
  SheetClose,
  SheetContent,
  SheetHeader,
  SheetFooter,
  SheetTitle,
  SheetDescription,
}

import * as React from "react"
import { Button, type ButtonProps } from "@/components/ui/button"
import { cn } from "@/lib/utils"

export interface ButtonMerlionProps extends ButtonProps {
  variant?: "primary" | "secondary"
}

const ButtonMerlion = React.forwardRef<HTMLButtonElement, ButtonMerlionProps>(
  ({ className, variant = "primary", children, ...props }, ref) => {
    return (
      <Button
        ref={ref}
        className={cn(
          "relative min-h-[3.5rem] px-8 font-body text-lg font-600 border-2 transition-all duration-medium ease-smooth overflow-hidden group",
          variant === "primary" && "bg-ui-terracotta text-nyonya-cream border-transparent hover:text-nyonya-cream",
