            <div className="flex flex-col md:flex-row gap-4 justify-center">
              <ButtonMerlion variant="primary" asChild>
                <Link href="#beans">Discover Our Beans</Link>
              </ButtonMerlion>
              <ButtonMerlion variant="secondary" asChild>
                <Link href="#tasting-room">Tasting Experience</Link>
