args.content:
import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import { produce } from 'immer'

export interface CartItem {
  sku: string
  quantity: number
  price: number
  name: string
  image?: string
}

interface CartState {
  items: CartItem[]
  reservationId: string | null
  expiresAt: string | null
  addItem: (item: CartItem) => void
  removeItem: (sku: string) => void
  updateQuantity: (sku: string, quantity: number) => void
  setReservation: (id: string, expiresAt: string) => void
  clearReservation: () => void
  clearCart: () => void
}

export const useCartStore = create<CartState>()(
  persist(
    (set) => ({
      items: [],
      reservationId: null,
      expiresAt: null,
      addItem: (item) =>
        set(
          produce((state) => {
            const existing = state.items.find((i: CartItem) => i.sku === item.sku)
            if (existing) {
              existing.quantity += item.quantity
            } else {
              state.items.push(item)
            }
          })
        ),
      removeItem: (sku) =>
        set(
          produce((state) => {
            const index = state.items.findIndex((i: CartItem) => i.sku === sku)
            if (index !== -1) {
              state.items.splice(index, 1)
            }
          })
        ),
      updateQuantity: (sku, quantity) =>
        set(
          produce((state) => {
            const item = state.items.find((i: CartItem) => i.sku === sku)
            if (item) {
              item.quantity = quantity
            }
          })
        ),
      setReservation: (id, expiresAt) => set({ reservationId: id, expiresAt }),
      clearReservation: () => set({ reservationId: null, expiresAt: null }),
      clearCart: () => set({ items: [], reservationId: null, expiresAt: null }),
    }),
    {
      name: 'merlion-cart',
    }
  )
)

import { NextResponse } from 'next/server';

export async function POST(request: Request) {
  const baseUrl = process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000';
  const body = await request.json();

  try {
    const res = await fetch(`${baseUrl}/api/v1/reservations`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
      body: JSON.stringify(body),
    });

    if (!res.ok) {
      const errorData = await res.json();
      return NextResponse.json(errorData, { status: res.status });
    }

    const data = await res.json();
    return NextResponse.json(data, { status: 201 });
  } catch (error) {
    return NextResponse.json({ error: 'Failed to connect to reservation service' }, { status: 500 });
  }
}

"use client"

import * as React from "react"
import { useStripe, useElements, PaymentElement } from "@stripe/react-stripe-js"
import { ButtonMerlion } from "./button-merlion"

export default function CheckoutForm() {
  const stripe = useStripe()
  const elements = useElements()

  const [message, setMessage] = React.useState<string | null>(null)
  const [isLoading, setIsLoading] = React.useState(false)

  React.useEffect(() => {
    if (!stripe) {
      return
    }

    const clientSecret = new URLSearchParams(window.location.search).get(
      "payment_intent_client_secret"
    )

    if (!clientSecret) {
      return
    }

    stripe.retrievePaymentIntent(clientSecret).then(({ paymentIntent }) => {
      switch (paymentIntent?.status) {
        case "succeeded":
          setMessage("Payment succeeded!")
          break
        case "processing":
          setMessage("Your payment is processing.")
          break
        case "requires_payment_method":
          setMessage("Your payment was not successful, please try again.")
          break
        default:
          setMessage("Something went wrong.")
          break
      }
    })
  }, [stripe])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()

    if (!stripe || !elements) {
      return
    }

    setIsLoading(true)

    const { error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        return_url: `${window.location.origin}/checkout`,
      },
    })

    if (error.type === "card_error" || error.type === "validation_error") {
      setMessage(error.message || "An error occurred")
    } else {
      setMessage("An unexpected error occurred.")
    }

    setIsLoading(false)
  }

  return (
    <form id="payment-form" onSubmit={handleSubmit} className="space-y-6">
      <PaymentElement id="payment-element" options={{ layout: "tabs" }} />
      <ButtonMerlion disabled={isLoading || !stripe || !elements} id="submit" className="w-full">
        {isLoading ? <div className="spinner" id="spinner">Processing...</div> : "Pay now"}
      </ButtonMerlion>
      {message && <div id="payment-message" className="text-red-500 text-sm text-center">{message}</div>}
    </form>
  )
}

"use client"

import React from "react"
import { loadStripe } from "@stripe/stripe-js"
import { Elements } from "@stripe/react-stripe-js"
import CheckoutForm from "@/components/merlion/checkout-form"
import { useCartStore } from "@/lib/cart-store"
import { useRouter } from "next/navigation"

const stripePromise = loadStripe(process.env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY!)

export default function CheckoutPage() {
  const [clientSecret, setClientSecret] = React.useState("")
  const { items, reservationId } = useCartStore()
  const router = useRouter()

  React.useEffect(() => {
    if (items.length === 0) {
        router.push('/')
        return;
    }

    if (!reservationId) {
        // Should probably reserve first, but assuming flow guarantees reservation
        return; 
    }

    // Create PaymentIntent as soon as the page loads
    fetch("/api/checkout", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ items, reservationId, email: "guest@example.com" }), // Email collection needed in UI
    })
      .then((res) => res.json())
      .then((data) => setClientSecret(data.clientSecret))
  }, [items, reservationId, router])

  const appearance = {
    theme: 'stripe' as const,
    variables: {
      colorPrimary: '#C77966',
      colorBackground: '#F8F3E6',
      colorText: '#3A2A1F',
    },
  };
  const options = {
    clientSecret,
    appearance,
  }

  return (
    <div className="min-h-screen bg-nyonya-cream flex items-center justify-center p-4">
      <div className="w-full max-w-md bg-white p-8 rounded-xl shadow-lg border border-kopi-brown/10">
        <h1 className="font-heading text-3xl text-ui-terracotta mb-6 text-center">Checkout</h1>
        {clientSecret && (
          <Elements options={options} stripe={stripePromise}>
            <CheckoutForm />
          </Elements>
        )}
      </div>
    </div>
  )
}

import { NextResponse } from 'next/server';

export async function POST(request: Request) {
  const baseUrl = process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000';
  const body = await request.json();

  try {
    const res = await fetch(`${baseUrl}/api/v1/orders/draft`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      },
      body: JSON.stringify({
        reservation_id: body.reservationId,
        email: body.email
      }),
    });

    if (!res.ok) {
        const error = await res.json();
        return NextResponse.json(error, { status: res.status });
    }

    const data = await res.json();
    return NextResponse.json({ clientSecret: data.client_secret });
  } catch (error) {
    return NextResponse.json({ error: 'Failed to create order' }, { status: 500 });
  }
}

