import * as React from "react"
import { Button, buttonVariants, type ButtonProps } from "@/components/ui/button"
import { cn } from "@/lib/utils"
import Link from "next/link"

export interface ButtonMerlionProps extends Omit<ButtonProps, 'variant'> {
  variant?: "primary" | "secondary"
  href?: string
}

const ButtonMerlion = React.forwardRef<HTMLButtonElement | HTMLAnchorElement, ButtonMerlionProps>(
  ({ className, variant = "primary", children, href, ...props }, ref) => {
    const classes = cn(
      buttonVariants({ variant: "default" }), // Start with base button styles
      "relative min-h-[3.5rem] px-8 font-body text-lg font-600 border-2 transition-all duration-medium ease-smooth overflow-hidden group",
      variant === "primary" && "bg-ui-terracotta text-nyonya-cream border-transparent hover:text-nyonya-cream hover:bg-ui-terracotta", // Override Shadcn hover
      variant === "secondary" && "bg-transparent border-ui-terracotta text-ui-terracotta hover:text-nyonya-cream hover:bg-transparent",
      className
    )

    const content = (
      <>
        {/* Hover Underlay Span */}
        <span 
          className={cn(
            "absolute inset-0 -z-10 transform scale-x-0 origin-right transition-transform duration-medium ease-smooth group-hover:scale-x-100 group-hover:origin-left group-focus:scale-x-100 group-focus:origin-left",
            variant === "primary" ? "bg-terracotta" : "bg-ui-terracotta"
          )}
        />
        <span className="relative z-10">{children}</span>
      </>
    )

    if (href) {
      return (
        <Link 
          href={href} 
          className={classes}
          // @ts-ignore - Link ref typing is tricky with forwardRef union, safe to ignore for internal usage
          ref={ref as any}
        >
          {content}
        </Link>
      )
    }

    return (
      <Button
        ref={ref as React.Ref<HTMLButtonElement>}
        className={cn(
          "relative min-h-[3.5rem] px-8 font-body text-lg font-600 border-2 transition-all duration-medium ease-smooth overflow-hidden group",
          variant === "primary" && "bg-ui-terracotta text-nyonya-cream border-transparent hover:text-nyonya-cream hover:bg-ui-terracotta",
          variant === "secondary" && "bg-transparent border-ui-terracotta text-ui-terracotta hover:text-nyonya-cream hover:bg-transparent",
          className
        )}
        // We handle variants manually via className to ensure overrides work
        variant="default" 
        {...props}
      >
        {content}
      </Button>
    )
  }
)
ButtonMerlion.displayName = "ButtonMerlion"

export { ButtonMerlion }

import { NextResponse } from "next/server";

// Simple in-memory rate limiter for demo/MVP purposes
// In production, use Redis (e.g., via upstash/ratelimit)
const rateLimit = new Map<string, number>();
const RATE_LIMIT_WINDOW = 60 * 1000; // 1 minute
const MAX_REQUESTS = 5; // 5 requests per minute

export async function POST(request: Request) {
  // 1. Content-Type Guard
  const contentType = request.headers.get("content-type");
  if (!contentType || !contentType.includes("application/json")) {
    return new NextResponse("Unsupported Media Type", { status: 415 });
  }

  // 2. Rate Limiting (IP-based)
  const ip = request.headers.get("x-forwarded-for") || "unknown";
  const now = Date.now();
  const windowStart = now - RATE_LIMIT_WINDOW;

  // Cleanup old entries
  // (In a real app, this would be handled by the store expiration)

  const requestTimestamps = rateLimit.get(ip) || [];
  const recentRequests = requestTimestamps.filter(t => t > windowStart);

  if (recentRequests.length >= MAX_REQUESTS) {
    return new NextResponse("Too Many Requests", { 
      status: 429,
      headers: { 'Retry-After': '60' }
    });
  }

  rateLimit.set(ip, [...recentRequests, now]);

  try {
    const body = await request.json();
    const { email, consent_marketing } = body;

    // 3. Input Validation
    if (!email || typeof email !== "string" || !email.includes("@")) {
      return NextResponse.json(
        { error: "Invalid email address" },
        { status: 400 }
      );
    }

    if (consent_marketing !== true) {
      return NextResponse.json(
        { error: "Consent is required" },
        { status: 400 }
      );
    }

    const baseUrl = process.env.NEXT_PUBLIC_API_BASE_URL || 'http://localhost:8000';

    // 4. Timeouts & Fetch
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 5000); // 5s timeout

    try {
      const res = await fetch(`${baseUrl}/api/v1/newsletter/subscribe`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        // 5. Payload Whitelisting (Don't just forward `body`)
        body: JSON.stringify({ 
          email, 
          consent_marketing 
        }),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (!res.ok) {
        // Log the error internally but don't leak upstream details to client
        console.error(`Newsletter API Error: ${res.status}`);
        return NextResponse.json(
          { error: "Subscription failed" }, 
          { status: res.status === 422 ? 422 : 502 }
        );
      }

      const data = await res.json();
      return NextResponse.json(data);

    } catch (fetchError) {
      clearTimeout(timeoutId);
      if (fetchError.name === 'AbortError') {
        return NextResponse.json({ error: "Service timeout" }, { status: 504 });
      }
      throw fetchError;
    }

  } catch (error) {
    console.error("Newsletter Proxy Error:", error);
    return NextResponse.json(
      { error: "Internal Server Error" },
      { status: 500 }
    );
  }
}

"use client"

import * as React from "react"
import { CardMerlion } from "@/components/merlion/card-merlion"
import { ButtonMerlion } from "@/components/merlion/button-merlion"
import { cn } from "@/lib/utils"

interface Event {
  id: string
  title: string
  description: string
  price_cents: number
  sessions: Session[]
}

interface Session {
  id: string
  starts_at: string
  capacity: number
  booked_count: number
}

export default function EventsPage() {
  const [events, setEvents] = React.useState<Event[]>([])
  const [loading, setLoading] = React.useState(true)

  React.useEffect(() => {
    fetch('/api/bookings') // BFF to backend GET /events
      .then(res => res.json())
      .then(data => {
        setEvents(data.data || [])
        setLoading(false)
      })
      .catch(err => {
        console.error(err)
        setLoading(false)
      })
  }, [])

  const handleBook = async (sessionId: string) => {
    // Simple prompt for MVP
    const email = prompt("Please enter your email to confirm booking:")
    if (!email) return

    try {
      const res = await fetch('/api/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ session_id: sessionId, email, quantity: 1 })
      })

      if (res.ok) {
        alert("Booking confirmed!")
        // Refresh
        window.location.reload()
      } else {
        alert("Booking failed. Session might be full.")
      }
    } catch (e) {
      alert("Error booking session")
    }
  }

  return (
    <main className="min-h-screen bg-nyonya-cream py-32 px-4">
      <div className="max-w-[85ch] mx-auto">
        <h1 className="font-heading text-5xl text-ui-terracotta mb-8 text-center">Cultural Gatherings</h1>
        <p className="text-center text-kopi-brown mb-16 max-w-[60ch] mx-auto">
          Join us for intimate sessions celebrating the heritage of coffee and culture in Singapore.
        </p>

        {loading ? (
          <div className="text-center">Loading gatherings...</div>
        ) : (
          <div className="grid gap-8">
            {events.map(event => (
              <CardMerlion key={event.id} className="p-8">
                <div className="flex flex-col md:flex-row justify-between gap-8">
                  <div>
                    <h2 className="text-3xl font-600 text-kopi-brown mb-2">{event.title}</h2>
                    <p className="text-kopi-brown/80 mb-4">{event.description}</p>
                    <div className="text-ui-gold font-decorative text-2xl">
                      <span className="sr-only">Price: </span>
                      {event.price_cents === 0 ? "Free" : `$${(event.price_cents / 100).toFixed(2)}`}
                    </div>
                  </div>

                  <div className="flex flex-col gap-4 min-w-[200px]">
                    <h3 className="font-600 text-sm uppercase tracking-widest text-ui-blue">Upcoming Sessions</h3>
                    {event.sessions.length > 0 ? (
                      event.sessions.map(session => (
                        <div key={session.id} className="flex items-center justify-between border-b border-kopi-brown/10 pb-2">
                          <div>
                            <div className="font-600">
                              {new Date(session.starts_at).toLocaleDateString()}
                            </div>
                            <div className="text-sm text-kopi-brown/60">
                              {new Date(session.starts_at).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </div>
                          </div>
                          <ButtonMerlion 
                            variant="secondary" 
                            size="sm"
                            disabled={session.booked_count >= session.capacity}
                            onClick={() => handleBook(session.id)}
                            aria-label={`Book session on ${new Date(session.starts_at).toLocaleDateString()}`}
                          >
                            {session.booked_count >= session.capacity ? "Full" : "Book"}
                          </ButtonMerlion>
                        </div>
                      ))
                    ) : (
                      <p className="text-sm italic text-kopi-brown/50">No upcoming sessions.</p>
                    )}
                  </div>
                </div>
              </CardMerlion>
            ))}
          </div>
        )}
      </div>
    </main>
  )
}

@import "tailwindcss";

@layer tokens {
